    private static AbstractBlock.Settings copyLootTable(Block block, boolean copyTranslationKey) {
        AbstractBlock.Settings lv = block.getSettings();
        AbstractBlock.Settings lv2 = AbstractBlock.Settings.create().lootTable(block.getLootTableKey());
        if (copyTranslationKey) {
            lv2 = lv2.overrideTranslationKey(block.getTranslationKey());
        }
        return lv2;
    }

    private static Block register(RegistryKey<Block> key, Function<AbstractBlock.Settings, Block> factory, AbstractBlock.Settings settings) {
        Block lv = factory.apply(settings.registryKey(key));
        return Registry.register(Registries.BLOCK, key, lv);
    }

    private static Block register(RegistryKey<Block> key, AbstractBlock.Settings settings) {
        return Blocks.register(key, Block::new, settings);
    }

    private static RegistryKey<Block> keyOf(String id) {
        return RegistryKey.of(RegistryKeys.BLOCK, Identifier.ofVanilla(id));
    }

    private static Block register(String id, Function<AbstractBlock.Settings, Block> factory, AbstractBlock.Settings settings) {
        return Blocks.register(Blocks.keyOf(id), factory, settings);
    }

    private static Block register(String id, AbstractBlock.Settings settings) {
        return Blocks.register(id, Block::new, settings);
    }

    static {
        for (Block lv : Registries.BLOCK) {
            for (BlockState lv2 : lv.getStateManager().getStates()) {
                Block.STATE_IDS.add(lv2);
                lv2.initShapeCache();
            }
        }
    }
