    private static Function<Item.Settings, Item> createBlockItemWithUniqueName(Block block) {
        return settings -> new BlockItem(block, settings.useItemPrefixedTranslationKey());
    }

    private static RegistryKey<Item> keyOf(String id) {
        return RegistryKey.of(RegistryKeys.ITEM, Identifier.ofVanilla(id));
    }

    private static RegistryKey<Item> keyOf(RegistryKey<Block> blockKey) {
        return RegistryKey.of(RegistryKeys.ITEM, blockKey.getValue());
    }

    public static Item register(Block block) {
        return Items.register(block, BlockItem::new);
    }

    public static Item register(Block block, Item.Settings settings) {
        return Items.register(block, BlockItem::new, settings);
    }

    public static Item register(Block block, UnaryOperator<Item.Settings> settingsOperator) {
        return Items.register(block, (Block blockx, Item.Settings settings) -> new BlockItem((Block)blockx, (Item.Settings)settingsOperator.apply((Item.Settings)settings)));
    }

    public static Item register(Block block, Block ... blocks) {
        Item lv = Items.register(block);
        for (Block lv2 : blocks) {
            Item.BLOCK_ITEMS.put(lv2, lv);
        }
        return lv;
    }

    public static Item register(Block block, BiFunction<Block, Item.Settings, Item> factory) {
        return Items.register(block, factory, new Item.Settings());
    }

    public static Item register(Block block, BiFunction<Block, Item.Settings, Item> factory, Item.Settings settings) {
        return Items.register(Items.keyOf(block.getRegistryEntry().registryKey()), (Item.Settings itemSettings) -> (Item)factory.apply(block, (Item.Settings)itemSettings), settings.useBlockPrefixedTranslationKey());
    }

    public static Item register(String id, Function<Item.Settings, Item> factory) {
        return Items.register(Items.keyOf(id), factory, new Item.Settings());
    }

    public static Item register(String id, Function<Item.Settings, Item> factory, Item.Settings settings) {
        return Items.register(Items.keyOf(id), factory, settings);
    }

    public static Item register(String id, Item.Settings settings) {
        return Items.register(Items.keyOf(id), Item::new, settings);
    }

    public static Item register(String id) {
        return Items.register(Items.keyOf(id), Item::new, new Item.Settings());
    }

    public static Item register(RegistryKey<Item> key, Function<Item.Settings, Item> factory) {
        return Items.register(key, factory, new Item.Settings());
    }

    public static Item register(RegistryKey<Item> key, Function<Item.Settings, Item> factory, Item.Settings settings) {
        Item lv = factory.apply(settings.registryKey(key));
        if (lv instanceof BlockItem) {
            BlockItem lv2 = (BlockItem)lv;
            lv2.appendBlocks(Item.BLOCK_ITEMS, lv);
        }
        return Registry.register(Registries.ITEM, key, lv);
    }
